import React, { useEffect, useState } from "react";
import { getWatchlist, updateWatchlist, getAnimeList } from "../services/api";
import { Box, Typography, Grid, Card, CardMedia, CardContent, CardActions, Button, AppBar, Toolbar } from "@mui/material";
import { useNavigate } from "react-router-dom";

const WatchlistPage = ({ setDarkMode, darkMode }) => {
  const [animeList, setAnimeList] = useState([]);
  const [watchlist, setWatchlist] = useState([]);
  const navigate = useNavigate();
  const userId = localStorage.getItem("username");

  useEffect(() => {
    getAnimeList().then(res => setAnimeList(res.data));
    getWatchlist(userId).then(res => setWatchlist(res.data.animeIds || []));
  }, [userId]);

  const removeFromWatchlist = async (id) => {
    const updated = watchlist.filter(animeId => animeId !== id);
    await updateWatchlist(userId, updated);
    setWatchlist(updated);
  };

  const myAnime = animeList.filter(a => watchlist.includes(a.id));

  return (
    <Box sx={{ bgcolor: "background.default", minHeight: "100vh" }}>
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h5" sx={{ flexGrow: 1, fontWeight: 700, letterSpacing: 2 }}>
            AnimeFlix - Watchlist
          </Typography>
          <Button color="inherit" onClick={() => navigate("/catalog")}>Catalog</Button>
          <Button color="inherit" onClick={() => {
            localStorage.clear();
            navigate("/login");
          }}>Logout</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ p: 3 }}>
        <Typography variant="h4" sx={{ mb: 3 }}>My Watchlist</Typography>
        <Grid container spacing={3}>
          {myAnime.map(anime => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={anime.id}>
              <Card sx={{ bgcolor: "#23272f", color: "#fff", borderRadius: 3 }}>
                <CardMedia
                  component="img"
                  height="340"
                  image={anime.imageUrl}
                  alt={anime.title}
                  sx={{ objectFit: "cover" }}
                  onClick={() => navigate(`/video/${anime.id}`)}
                />
                <CardContent>
                  <Typography variant="h6" fontWeight={700}>{anime.title}</Typography>
                  <Typography variant="body2">{anime.genre} | {anime.year}</Typography>
                  <Typography variant="body2" sx={{ mt: 1 }}>{anime.description.substring(0, 70)}...</Typography>
                </CardContent>
                <CardActions>
                  <Button variant="contained" color="secondary" onClick={() => removeFromWatchlist(anime.id)}>
                    Remove
                  </Button>
                  <Button variant="contained" color="secondary" onClick={() => navigate(`/video/${anime.id}`)}>
                    Watch Now
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
};

export default WatchlistPage;
