import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../services/api";
import { Button, TextField, Box, Typography, Paper } from "@mui/material";

const LoginPage = ({ setDarkMode }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const res = await login(username, password);
      // Debug: Log the response
      console.log("Login API response:", res);
      if (!res.data || !res.data.token) {
        setError("Login failed: No token returned from backend");
        return;
      }
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("role", res.data.role);
      localStorage.setItem("username", username);
      navigate("/catalog");
    } catch (err) {
      // Debug: Log the error
      console.error("Login error:", err);
      if (err.response && err.response.data) {
        // Try to extract a message if available, otherwise stringify the error object
        const backendError = typeof err.response.data === 'string'
          ? err.response.data
          : JSON.stringify(err.response.data);
        setError("Login failed: " + backendError);
      } else {
        setError("Invalid username or password");
      }
    }
  };

  return (
    <Box sx={{ minHeight: "100vh", display: "flex", alignItems: "center", justifyContent: "center", bgcolor: "background.default" }}>
      <Paper elevation={6} sx={{ p: 4, minWidth: 320 }}>
        <Typography variant="h4" align="center" gutterBottom>Sign In</Typography>
        <form onSubmit={handleLogin}>
          <TextField label="Username" fullWidth margin="normal" value={username} onChange={e => setUsername(e.target.value)} required />
          <TextField label="Password" type="password" fullWidth margin="normal" value={password} onChange={e => setPassword(e.target.value)} required />
          {error && <Typography color="error">{error}</Typography>}
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>Login</Button>
        </form>
        <Box sx={{ mt: 2, textAlign: "center" }}>
          <Button onClick={() => navigate("/signup")}>Don't have an account? Sign Up</Button>
        </Box>
        <Box sx={{ mt: 2, textAlign: "center" }}>
          <Button onClick={() => setDarkMode(dm => !dm)}>Toggle Dark Mode</Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default LoginPage;
