import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getAnimeById } from "../services/api";
import { Box, Typography, Button, AppBar, Toolbar, Paper } from "@mui/material";

const VideoPage = ({ setDarkMode, darkMode }) => {
  const { id } = useParams();
  const [anime, setAnime] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    getAnimeById(id).then(res => setAnime(res.data));
  }, [id]);

  if (!anime) return <Typography>Loading...</Typography>;

  return (
    <Box sx={{ bgcolor: "background.default", minHeight: "100vh" }}>
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h5" sx={{ flexGrow: 1, fontWeight: 700, letterSpacing: 2 }}>
            AnimeFlix - {anime.title}
          </Typography>
          <Button color="inherit" onClick={() => navigate("/catalog")}>Catalog</Button>
          <Button color="inherit" onClick={() => navigate("/watchlist")}>Watchlist</Button>
          <Button color="inherit" onClick={() => {
            localStorage.clear();
            navigate("/login");
          }}>Logout</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ p: 3, display: "flex", flexDirection: "column", alignItems: "center" }}>
        <Paper elevation={6} sx={{ p: 2, mb: 3, maxWidth: 800, width: "100%" }}>
          <Typography variant="h4" sx={{ mb: 1 }}>{anime.title}</Typography>
          <Typography variant="subtitle1" sx={{ mb: 2 }}>{anime.genre} | {anime.year}</Typography>
          <Typography variant="body1" sx={{ mb: 2 }}>{anime.description}</Typography>
          <video controls width="100%" poster={anime.imageUrl} style={{ borderRadius: 8 }}>
            <source src={anime.videoUrl} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </Paper>
      </Box>
    </Box>
  );
};

export default VideoPage;
