import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getAnimeList, getWatchlist, updateWatchlist } from "../services/api";
import { Box, Typography, Grid, Card, CardMedia, CardContent, CardActions, Button, IconButton, AppBar, Toolbar, Switch, TextField, Snackbar, Alert as MuiAlert } from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";

const CatalogPage = ({ setDarkMode, darkMode }) => {
  // Debug: Log when CatalogPage renders
  console.log("CatalogPage rendered");
  const [animeList, setAnimeList] = useState([]);
  const [search, setSearch] = useState("");
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [watchlist, setWatchlist] = useState([]);
  const navigate = useNavigate();
  const userId = localStorage.getItem("username");

  useEffect(() => {
    const fetchAnime = () => {
      getAnimeList()
        .then(res => {
          console.log("Fetched anime list:", res.data);
          setAnimeList(res.data);
        })
        .catch(err => {
          console.error("Error fetching anime list:", err);
        });
    };
    fetchAnime();
    const interval = setInterval(fetchAnime, 10000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (userId) {
      getWatchlist(userId)
        .then(res => {
          console.log("Fetched watchlist:", res.data);
          setWatchlist(res.data.animeIds || []);
        })
        .catch(err => {
          console.error("Error fetching watchlist:", err);
        });
    } else {
      console.warn("No userId found in localStorage");
    }
  }, [userId]);

  const handleAddToWatchlist = async (animeId) => {
    if (!userId) {
      setSnackbar({ open: true, message: 'You must be logged in to add to watchlist.', severity: 'error' });
      return;
    }
    if (watchlist.includes(animeId)) {
      setSnackbar({ open: true, message: 'Already in watchlist!', severity: 'info' });
      return;
    }
    const updated = [...watchlist, animeId];
    await updateWatchlist(userId, updated);
    setWatchlist(updated);
    setSnackbar({ open: true, message: 'Added to watchlist!', severity: 'success' });
  };

  const handleRemoveFromWatchlist = async (animeId) => {
    if (!userId) return;
    const updated = watchlist.filter(id => id !== animeId);
    await updateWatchlist(userId, updated);
    setWatchlist(updated);
    setSnackbar({ open: true, message: 'Removed from watchlist!', severity: 'info' });
  };

  const handleCloseSnackbar = () => setSnackbar({ ...snackbar, open: false });

  const filtered = animeList.filter(a =>
    a.title.toLowerCase().includes(search.toLowerCase()) ||
    a.genre.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box sx={{ bgcolor: "background.default", minHeight: "100vh" }}>
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h5" sx={{ flexGrow: 1, fontWeight: 700, letterSpacing: 2 }}>
            AnimeFlix
          </Typography>
          <Switch checked={darkMode} onChange={() => setDarkMode(dm => !dm)} color="secondary" />
          <Button color="inherit" onClick={() => navigate("/watchlist")}>Watchlist</Button>
          <Button color="inherit" onClick={() => {
            localStorage.clear();
            navigate("/login");
          }}>Logout</Button>
        </Toolbar>
      </AppBar>
      <Box sx={{ p: 3 }}>
        <TextField
          label="Search anime or genre"
          fullWidth
          value={search}
          onChange={e => setSearch(e.target.value)}
          sx={{ mb: 3 }}
        />
        <Grid container spacing={3}>
          {filtered.map(anime => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={anime.id}>
              <Card sx={{ bgcolor: "#23272f", color: "#fff", borderRadius: 3 }}>
                <CardMedia
                  component="img"
                  height="340"
                  image={anime.imageUrl}
                  alt={anime.title}
                  sx={{ objectFit: "cover" }}
                  onClick={() => navigate(`/video/${anime.id}`)}
                />
                <CardContent>
                  <Typography variant="h6" fontWeight={700}>{anime.title}</Typography>
                  <Typography variant="body2">{anime.genre} | {anime.year}</Typography>
                  <Typography variant="body2" sx={{ mt: 1 }}>{anime.description.substring(0, 70)}...</Typography>
                </CardContent>
                <CardActions>
                  <IconButton color={watchlist.includes(anime.id) ? "error" : "secondary"} onClick={() => watchlist.includes(anime.id) ? handleRemoveFromWatchlist(anime.id) : handleAddToWatchlist(anime.id)}>
                    <FavoriteIcon />
                  </IconButton>
                  <Button variant="contained" color="secondary" onClick={() => navigate(`/video/${anime.id}`)}>
                    Watch Now
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
      <Snackbar open={snackbar.open} autoHideDuration={2500} onClose={handleCloseSnackbar} anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}>
        <MuiAlert elevation={6} variant="filled" onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </MuiAlert>
      </Snackbar>
    </Box>
  );
};

export default CatalogPage;
